package chapter2;

public class Test7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		a++;
		System.out.println("a = " + a);
		++a;
		System.out.println("a = " + a);
		
		int b = 10;
		b--;
		System.out.println("b = " + b);
		--b;
		System.out.println("b = " + b);
		
		int n = 10;
		System.out.println("n++ = " + n++);
		System.out.println("++n = " + ++n);
		n++;
		++n;
		System.out.println("n = " + n);

	}

}
